            var $cmp = self.$ctx;
            var $capsDiv = $cmp.find("#capabilitiesDiv");
            var $capsConfigCell = self.el['$capsConfigCell' + columnData.number];
            var $capsEngineDrivetypeDiv = $('.engineDrivetypeLabelDiv', $capsConfigCell);
