            var self = this;
            self.log('[Cnt_tbl_fs_2.deactivateSection] Method starting.');

            $('tr[data-category="' + myCategory + '"]').removeClass('active');

            self.log('[Cnt_tbl_fs_2.deactivateSection] Section deactivated: ' + myCategory + '.');
